package javaarray;

public class PracticeArray2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name[] = new String[6];

		name[0] = "Murali";
		name[1] = "Kevin";
		name[2] = "Nila";
		name[3] = "Kavya";
		name[4] = "Mahi";
		name[5] = "Dhinesh";
		for (int i = 0; i < name.length; i++) {
			System.out.println("Name [" + i + "]=" + name[i]);
		}
	}

}
